function fixPixnetPlayerID(){var objects=document.getElementsByTagName("object");var objectIDs=new Array;var len=objects.length;for(var i=0;i<len;i++){if(objects[i].id.indexOf("PixnetPlayer")==0){var objid=objects[i].id;if(objectIDs[objid]==undefined){objectIDs[objid]=1}else objectIDs[objid]++}}for(var i=0;i<len;i++){if(objectIDs[objects[i].id]>1){objects[i].id+="_"+objectIDs[objects[i].id]++;objects[i].parentNode.innerHTML=objects[i].parentNode.innerHTML}}}